insert into producto values(60004, 60000, 126.00, 40, 'Theraflu TD resfriado común y dolor de cabeza sabor limón 10 sobres', 'Auxiliar en el alivio de los síntomas del resfriado común y dolor de cabeza, como: reducción temporal de fiebre y escalofríos, dolores musculares, congestión nasal y de senos paranasales. Sabor Limón');
insert into producto values(60005, 60000, 30.50, 50, 'Jabón de tocador Asepxia exfoliante extremo 100 g', 'Recomendado para la piel mixta. Exfolia y limpia tu piel a profundidad, mientras ayuda a combatir imperfecciones y puntos negros. Su nueva fórmula Hidro-Force ayuda a disminuir el exceso de grasa, destapando los poros a profundidad. Potencializado con 2% de ácido salicílico y esferas de jojoba. ');
